function out = RRoverlapconvexpand(in,kernel)%% out = expand(in);	return an image expanded to double size,% out = expand(in, kernel); specify 1-D kernel with unity sum.if nargin == 1,	% Default kernel 	kernel = [0.05 0.25 0.4 0.25 0.05]; end;		[ysize xsize] = size(in);kernel = kernel*2;			% kernel sum=2 to account for padding.tmp = zeros(ysize,2*xsize);		% First double the widthk = 1:xsize;tmp(:,2*k) = in(:,k);tmp = RRoverlapconv(kernel,tmp);		% ..and filter horizontally. out = zeros(2*ysize,2*xsize);	% Next double the heightk = 1:ysize;out(2*k,:) = tmp(k,:);out = RRoverlapconv(kernel',out);		% ..and filter vertically.return